import React from 'react'
import './App.css';

class Jagdish extends React.Component{
  state = {
    lateprice : '',
    spaftersell : '',
    stock : '',
    leststock : '',
    percentage : '',
  }

  chnagelateValueHandeler = (event) => {
    this.setState({
        lateprice : event.target.value,
    })
}
  chnagespValueHandeler = (event) => {
  this.setState({
      spaftersell : event.target.value,
  })
}
  chnagestockValueHandeler = (event) => {
  this.setState({
      stock : event.target.value,
  })
}
  chnagelesValueHandeler = (event) => {
  this.setState({
      leststock : event.target.value,
  })
}
chnagepercentageHandeler = (event) => {
  this.setState({
      percentage : event.target.value ,
  })
}

  render() {
    const fristinput = <input type="number" onChange={this.chnagelateValueHandeler} placeholder="ટોટલ માલ "></input>
    const secondinput = <input type="number" onChange={this.chnagespValueHandeler} placeholder="મૂળ કિંમત "></input>
    const thirdinput = <input type="number" onChange={this.chnagestockValueHandeler} placeholder="આઉટની ટકાવારી% "></input>
    const fourinput = <input type="number" onChange={this.chnagelesValueHandeler} placeholder="વેચાણ કિંમત "></input>
    const fiveinput = <input type="number" onChange={this.chnagepercentageHandeler} placeholder="વેચાણમાં લેસની ટકાવારી% "></input>

    const mulamaount = this.state.lateprice * this.state.spaftersell
    const less = this.state.lateprice * this.state.stock / 100
    const less1 = this.state.lateprice - (this.state.lateprice * this.state.stock / 100)
    const sellamount = (this.state.lateprice - (this.state.lateprice * this.state.stock / 100)) * this.state.leststock
    const prsellamount = (((this.state.lateprice - (this.state.lateprice * this.state.stock / 100)) * this.state.leststock) * this.state.percentage) / 100
    const sellammount2 = sellamount - prsellamount
    const aftersel = mulamaount - (sellamount - prsellamount)
    const newsellrate = (mulamaount - (sellamount - prsellamount)) / less

    const mulamaount1 = <h2>પડતર કિંમત :- {mulamaount}</h2>
    const le = <h2>આઉટમાં બચેલો  માલ  :- {less}</h2>
    const le1 = <h2>વેચેલ માલ :- {less1}</h2>
    const sellamount1 = <h2>વેચેલ માલની કિંમત લેસ સાથે :- {sellammount2}</h2>
    const aftersel1 = <h2>આઉટમાં બચેલા માલની કિંમત :- {aftersel}</h2>
    const newsellrate1 = <h2>આઉટમાં બચેલા માલની કિંમત કેરેટમાં :- {newsellrate}</h2>

    const style = {
      display: 'inline-block',
      padding: '16px',
      margin: '16px',
      border: '1px solid black',
      textAlign: 'center',
      backgroundColor: '',
  }
  const styleforbox = {
      backgroundColor: '#00FFFF',
  }

    return (
      <div>
        {fristinput}
        {secondinput}
        {thirdinput}
        {fourinput}
        {fiveinput}
        <div style = {style}>{mulamaount1}</div>
        <div style = {style}>{le1}</div>
        <div style = {style}>{sellamount1}</div>
        <div style = {style}>{le}</div>
        <div style = {style}>{aftersel1}</div>
        <div style = {style}>{newsellrate1}</div>
      </div>
    )
  }
}

export default Jagdish;
