import React from 'react'
import './App.css';

class Jagdish extends React.Component{
  state = {
    lateprice : '',
    spaftersell : '',
    stock : '',
    leststock : '',
    percentage : '',
  }

  chnagelateValueHandeler = (event) => {
    this.setState({
        lateprice : event.target.value,
    })
}
  chnagespValueHandeler = (event) => {
  this.setState({
      spaftersell : event.target.value,
  })
}
  chnagestockValueHandeler = (event) => {
  this.setState({
      stock : event.target.value,
  })
}
  chnagelesValueHandeler = (event) => {
  this.setState({
      leststock : event.target.value,
  })
}
chnagepercentageHandeler = (event) => {
  this.setState({
      percentage : event.target.value ,
  })
}

  render() {
    const fristinput = <input type="text" onChange={this.chnagelateValueHandeler}></input>
    const secondinput = <input type="text" onChange={this.chnagespValueHandeler}></input>
    const thirdinput = <input type="text" onChange={this.chnagestockValueHandeler}></input>
    const fourinput = <input type="text" onChange={this.chnagelesValueHandeler}></input>
    const fiveinput = <input type="text" onChange={this.chnagepercentageHandeler}></input>

    const mulamaount = this.state.lateprice * this.state.spaftersell
    const less = this.state.lateprice * this.state.stock / 100
    const less1 = this.state.lateprice - (this.state.lateprice * this.state.stock / 100)
    const sellamount = (this.state.lateprice - (this.state.lateprice * this.state.stock / 100)) * this.state.leststock
    const prsellamount = (((this.state.lateprice - (this.state.lateprice * this.state.stock / 100)) * this.state.leststock) * this.state.percentage) / 100
    const sellammount2 = sellamount - prsellamount
    const aftersel = mulamaount - (sellamount - prsellamount)
    const newsellrate = (mulamaount - (sellamount - prsellamount)) / less

    const mulamaount1 = <h1>પડતર કિંમત :- {mulamaount}</h1>
    const le = <h1>આઉટમાં બચેલો  માલ  :- {less}</h1>
    const le1 = <h1>વેચેલ માલ :- {less1}</h1>
    const sellamount1 = <h1>વેચેલ માલની કિંમત લેસ સાથે :- {sellammount2}</h1>
    const aftersel1 = <h1>આઉટમાં બચેલા માલની કિંમત :- {aftersel}</h1>
    const newsellrate1 = <h1>આઉટમાં બચેલા માલની કિંમત કેરેટમાં :- {newsellrate}</h1>

    return (
      <div>
        {fristinput}
        {secondinput}
        {thirdinput}
        {fourinput}
        {fiveinput}
        {mulamaount1}
        {le1}
        {sellamount1}
        {le}
        {aftersel1}
        {newsellrate1}
      </div>
    )
  }
}

export default Jagdish;
